import React, { Fragment, useEffect, useState } from 'react'
import Editmemes from "./editmeme";
import { makeStyles } from '@material-ui/core/styles';
import { Grid } from "@material-ui/core";
import { Button } from "@material-ui/core";
import MemeCard from"./memecard";
import DeleteIcon from '@material-ui/icons/Delete';

const useStyles = makeStyles((theme) => ({
    btnstyle: {
        margin: '8px 0'
    },

    button: {
        margin: theme.spacing(1),
    },
    alignment: {
        justifyItems: 'center'
    }
}));



export default function Listmemes() {
    const [memes, setMemes] = useState([]);
    const classes = useStyles();
    const deleteMeme = async id =>{
        try{
           const deleteMeme = await fetch(`http://localhost:5000/memes/${id}`,{
               method: "DELETE"
           });
           setMemes(memes.filter(meme => meme.meme_id!==id));
           console.log(deleteMeme);
        }catch(err){
            console.error(err.message);
        }
    }

    const getmemes = async () => {
       try {
        const response = await fetch("http://localhost:5000/memes");
        const jsonData = await response.json();
        memes.sort((a, b) => (a.meme_id>b.meme_id) ? 1 : -1)
        setMemes(jsonData);
        // console.log(jsonData);
       } catch(err){
           console.error(err.message);
       }
    };
    useEffect(()=>{
        getmemes();
    }, []);
    console.log(memes);

    const getMemeCard = (meme, idx) => {
        return (
            <Fragment>
            <Grid key = {idx} item xs = {12} sm = {6} lg = {4} >
                <MemeCard meme={meme}/>
                <Grid container justify="center">
                <h1><Editmemes meme={meme}/></h1>
                <Button variant="contained" color="secondary" onClick={()=> deleteMeme(meme.meme_id)} className = {classes.button} endIcon = {<DeleteIcon />}>
                   Delete
                </Button>
            </Grid>
            </Grid>

            </Fragment>
        );
    }

    return(
    <Fragment> 
        {" "}
        <Grid container spacing={2}>
                {memes.map((meme,idx) =>(getMemeCard(meme,idx)))}
        </Grid>
    </Fragment>
    );
}
