import React, {useState} from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogContent from '@material-ui/core/DialogContent';
import { makeStyles } from '@material-ui/core/styles';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Grid from '@material-ui/core/Grid';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';

const useStyles = makeStyles((theme) => ({
    btnstyle: {
        margin: '8px 0'
    },

    button: {
        margin: theme.spacing(1),
    },
    alignment: {
        justifyItems: 'center'
    }
}));


export default function FormDialog ({ meme }){

    const classes = useStyles();

    const [memes, setMemes] = useState([]);
    // console.log(meme.meme_id);

    const deleteMeme = async id =>{
        try{
           const deleteMeme = await fetch(`http://localhost:5000/memes/${id}`,{
               method: "DELETE"
           });
           setMemes(memes.filter(meme => meme.meme_id!==id));
           console.log(deleteMeme);
        }catch(err){
            console.error(err.message);
        }
    }
    const getmemes = async () => {
        try {
         const response = await fetch("http://localhost:5000/memes");
         const jsonData = await response.json();

         setMemes(jsonData);
         // console.log(jsonData);
        } catch(err){
            console.error(err.message);
        }
     };
     useEffect(()=>{
         getmemes();
     }, []);
  return (
    <Grid container justify="center">
        <Button variant="contained" color="primary" className = {classes.button} endIcon = {<EditIcon />}>
            Edit
        </Button>
        <Button variant="contained" color="secondary" onClick={()=> deleteMeme(meme.meme_id)} className = {classes.button} endIcon = {<DeleteIcon />}>
            Delete
        </Button>
    </Grid>
  );
}
