import React, { Fragment, useEffect, useState } from 'react'
import Editmemes from "./editmeme";

export default function Listmemes() {
    const [memes, setMemes] = useState([]);

    const deleteMeme = async id =>{
        try{
           const deleteMeme = await fetch(`http://localhost:5000/memes/${id}`,{
               method: "DELETE"
           });
           setMemes(memes.filter(meme => meme.meme_id!==id));
           console.log(deleteMeme);
        }catch(err){
            console.error(err.message);
        }
    }

    const getmemes = async () => {
       try {
        const response = await fetch("http://localhost:5000/memes");
        const jsonData = await response.json();

        setMemes(jsonData);
        // console.log(jsonData);
       } catch(err){
           console.error(err.message);
       }
    };
    useEffect(()=>{
        getmemes();
    }, []);
    // console.log(memes);
    return(
    <Fragment> 
        {" "}
        <img src="https://api.memegen.link/images/buzz/memes/memes_everywhere.png?token=6c7ek23718r0gwdt254l" alt="" />
        <table className="table mt-5 text-center">
            <tbody>
                {memes.map(meme =>(
                    <tr key={meme.meme_id}>
                        <div className="card text-center" style={{width: 1600}}>
                        <h1>{meme.meme_author}</h1>
                        <h1>{meme.meme_caption}</h1>
                        <h1></h1>
                        <img src="{meme.meme_url}" alt="" />
                        <h1><Editmemes meme={meme}/></h1>
                        <h1>
                            <button 
                               className="btn btn-danger"
                               onClick={()=> deleteMeme(meme.meme_id)}
                            >
                                Delete
                            </button>
                        </h1>
                        </div>
                    </tr>
                ))}
            </tbody>
        </table>
    </Fragment>
    );
}
