import React, { Fragment, useEffect, useState } from 'react'
import Editmemes from "./editmeme";

export default function Listmemes() {
    const [memes, setMemes] = useState([]);

    const deleteMeme = async id =>{
        try{
           const deleteMeme = await fetch(`http://localhost:5000/memes/${id}`,{
               method: "DELETE"
           });
           setMemes(memes.filter(meme => meme.meme_id!==id));
           console.log(deleteMeme);
        }catch(err){
            console.error(err.message);
        }
    }

    const getmemes = async () => {
       try {
        const response = await fetch("http://localhost:5000/memes");
        const jsonData = await response.json();

        setMemes(jsonData);
        // console.log(jsonData);
       } catch(err){
           console.error(err.message);
       }
    };
    useEffect(()=>{
        getmemes();
    }, []);
    // console.log(memes);
    return(
    <Fragment> 
        {" "}
        <table className="table mt-5 text-center">
           <thead>
                <tr>
                   <th>Description</th> 
                   <th>Edit</th>
                   <th>Delete</th>
                </tr>
            </thead>
            <tbody>
                {memes.map(meme =>(
                    <tr key={meme.meme_id}>
                        <td>{meme.meme_author}</td>
                        <td>{meme.meme_caption}</td>
                        <td><Editmemes meme={meme}/></td>
                        <td>
                            <button 
                               className="btn btn-danger"
                               onClick={()=> deleteMeme(meme.meme_id)}
                            >
                                Delete
                            </button>
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    </Fragment>
    );
}
