    const [meme_url,setMemeurl] = useState("https:/......");

    const onSubmitForm = async e =>{
      e.preventDefault();
      try{
        const body = { meme_author };
        const response = fetch("http://localhost:5000/memes",)

      }catch(err){
        console.error(err.message);
      }
    }


    return(
      <Fragment>
          <h1 className="text-center mt-5" >PERN MEME</h1>
          <form className="mt-5">
            <input 
              type="text" 
              className="form-control" 
              value={meme_author} 
              onChange={e => setMemeauthor(e.target.value)}
            />
            <input 
              type="text" 
              className="form-control" 
              value={meme_caption}
              onChange={e => setMemecaption(e.target.value)}
            />
            <input 
              type="text" 
              className="form-control" 
              value={meme_url}
              onChange={e => setMemeurl(e.target.value)}
            />
            <button className="btn btn-success">Add</button>
          </form>
      </Fragment>
    );
}
